Ext.define('MediLab.controller.TestMenuController', {
    extend: 'Ext.app.Controller',
    alias: 'controller.testmenucontroller',
    config: {
        refs: {
            testsearchpanel: '#testsearchpanel',
            testbynamelist: '#testbynamelist'
        },

        control: {
            "#testMenuList": {
                disclose: 'onTestMenuListDisclosure'
            },
            "button": {
                tap: 'onButtonTap'
            }
        }
    },

    onTestMenuListDisclosure: function(list, record, target, index, e, options) {
        //alert("The Test Portfolio list has been disclosed");
        switch(index) {
            case 0:
            Ext.Viewport.setActiveItem({xtype: 'testbynamelist'});
            break;

            case 1:
            Ext.Viewport.setActiveItem({xtype: 'bykeywordlist'});
            break;

            case 2:
            Ext.Viewport.setActiveItem({xtype: 'pickertest'});
            break;

            case 4:
            Ext.Viewport.setActiveItem({xtype: 'bydiseaselist'});

        }
    },

    onButtonTap: function(button, e, options) {

        button.up('testsearchpanel').push({
            xtype:'testbynamelist',
            title: 'Test by Name'
        });
    }

});